import React, { useEffect, useState } from 'react';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import AddEvent from './AddEvent';
import Modal from '@material-ui/core/Modal';
import firebase from '../../firebase';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles((theme) => ({
    text: {
        textTransform: 'none',
    },
    paper: {
        height: 100,
        width: '100%',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
    },
    widthMax: {
        maxWidth: 345,
    },
}));

const Event = () => {
    const classes = useStyles();
    const [open, setOpen] = useState(false);
    const [title, setTitle] = useState([])
    const [description, setDescription] = useState([])
    const [date, setDate] = useState([])
    useEffect(() => {
        firebase.getCurrentUserDescription().then(setDescription)
        firebase.getCurrentUserTitle().then(setTitle)
        firebase.getCurrentUserDate().then(setDate)
    })
    const handleClose = () => {
        setOpen(false)
    }
    const handleOpen = () => {
        setOpen(true)
    }
    return (
        <React.Fragment>
            <Grid spacing={3} >
                <Grid item xs={12}>
                    <Paper className={classes.paper}>
                        <Button size="medium" color="primary" variant="contained" className={classes.text} onClick={handleOpen}>
                            Add new Event
                        </Button>
                    </Paper>
                </Grid>
            </Grid>
            {date}
            <Modal
                open={open}
                onClose={handleClose}
                aria-labelledby="simple-modal-title"
                aria-describedby="simple-modal-description"
                style={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}
            >
                <AddEvent onClose={handleClose} />
            </Modal>
            <Card className={classes.widthMax}>
                <CardActionArea>
                    <CardContent>
                        <Typography gutterBottom variant="h5" component="h2">
                            {title}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" component="p">
                            {description}
                        </Typography>
                    </CardContent>
                </CardActionArea>
                <CardActions>
                    <Button size="small" color="primary">
                        Share
                    </Button>
                    <Button size="small" color="primary">
                        Learn More
                    </Button>
                </CardActions>
            </Card>
        </React.Fragment>
    )
}
export default Event;
