import app from "firebase/app";
import "firebase/auth";
import "firebase/firestore";

// For Firebase JS SDK v7.20.0 and later, measurementId is optional
const firebaseConfig = {
    apiKey: "AIzaSyA4Ie918eDFKCus2bbBqicVXVUnR4Ih-kk",
    authDomain: "testproject-5c3fd.firebaseapp.com",
    projectId: "testproject-5c3fd",
    storageBucket: "testproject-5c3fd.appspot.com",
    messagingSenderId: "854663840169",
    appId: "1:854663840169:web:5c29535fa95583c5f084b8",
    measurementId: "G-9RYWCQKJKQ"
};

// export const auth = firebase.auth();
// export const firestore = firebase.firestore();
const fire = app.initializeApp(firebaseConfig);
class Firebase {
    constructor() {
        // const fire = app.initializeApp(firebaseConfig);
        this.auth = app.auth();
        this.db = app.firestore()
    }
    login(email, password) {
        return this.auth.signInWithEmailAndPassword(email, password)
    }
    logout() {
        return this.auth.signOut()
    }
    async register(firstName, lastName, phoneNumber, acceptTerms, email, password) {
        await this.auth.createUserWithEmailAndPassword(email, password)
        return this.auth.currentUser.updateProfile({ displayName: firstName + ' ' + lastName, phoneNumber: phoneNumber, acceptTerms: acceptTerms, email: email, password: password })
    }
    addEvent(title, description, date) {
        if (!this.auth.currentUser) {
            return alert('Not authorised')
        }
        return this.db.doc(`users_codedamn_video/${this.auth.currentUseruid}`).push().set({ description, title, date })
        fire.database().ref('description').push().set({ title, description,date })
    }
    isInitialized() {
        return new Promise(resolve => {
            this.auth.onAuthStateChanged(resolve)
        })
    }
    getCurrentUsername() {
        return this.auth.currentUser && this.auth.currentUser.displayName
    }
    async getCurrentUserDescription() {
        const description = await this.db.doc(`users_codedamn_video/${this.auth.currentUseruid}`).get()
        return description.get('description')
    }
    async getCurrentUserTitle() {
        const description = await this.db.doc(`users_codedamn_video/${this.auth.currentUseruid}`).get()
        return description.get('title')
    }
    async getCurrentUserDate() {
        const description = await this.db.doc(`users_codedamn_video/${this.auth.currentUseruid}`).get()
        return description.get('date')
    }
}
export default new Firebase;
